import Hero from './Hero'
import { Link } from 'react-router-dom'
import ReactImageFallback from "react"


const MovieCard = ({ movie }) => {
    const posterUrl = `https://image.tmdb.org/t/p/w500${movie.poster_path}`
    const detailUrl = `/movies/${movie.id}`

    return(
        <div className="col-lg-3 col-md-3 col-6 my-4">
         <div className="card">
          <ReactImageFallback
            src={posterURL}
            fallbackImage="/Users/amelia/Desktop/React201-MovieApp/movie-app/src/images/No-image-found.jpeg"
            className="card-img-top"
            alt={movie.original_title}
           />
         </div>
        <div className="card">
          <img src={posterUrl} className="card-img-top" alt={movie.original_title}></img>
          <div className="card-body">
            <h5 className="card-title">{movie.original_title}</h5>
            <Link to={ detailUrl } className="btn btn-primary">Show Details</Link>
          </div>
        </div>
     </div>
    )
}




const SearchView = ({keyword, searchResults})=>{
    const title = `You are searching for ${keyword}`

    const resultsHtml = searchResults.map((obj, i) => {
        return <MovieCard movie = {obj} key = {i}/>
    })




    return(
    <div>
        <Hero text={title}/>
        {resultsHtml &&
        <div className = "container">
            <div className = "row">
                {resultsHtml}
            </div>
        </div>}
    </div>
    )
  }
    // {resultsHTML.length === 0 && (
