import Hero from './Hero'
import { Link } from 'react-router-dom'

// tmdb api key =267c28afad418890f877e7f790b0fda9
// url= 'https://api.themoviedb.org/3/search/movie?query=Jack+Reacher&api_key=267c28afad418890f877e7f790b0fda9'


const MovieCard = ({ movie }) => {
    const posterUrl = `https://image.tmdb.org/t/p/w500${movie.poster_path}`
    const detailUrl = `/movies/${movie.id}`

    return(
     <div className = "col-lg-3 col-md-3 col-2 my-4">
        <div className="card">
          <img src={posterUrl} className="card-img-top" alt={movie.original_title}></img>
          <div className="card-body">
            <h5 className="card-title">{movie.original_title}</h5>
            <Link to="/movies/id" className="btn btn-primary">Show Details</Link>
          </div>
        </div>
     </div>
    )
}




const SearchView = ({keyword, searchResults})=>{
    const title = `You are searching for ${keyword}`

    const resultsHtml = searchResults.map((obj, i) => {
        return <MovieCard movie = {obj} key = {i}/>
    })




    return(
    <div>
        <Hero text={title}/>
        {resultsHtml &&
        <div className = "container">
            <div className = "row">
                {resultsHtml}
            </div>
        </div>}
    </div>
    )
  }

  export default SearchView
